# Definition for singly-linked list.
# class ListNode:
#     def __init__(self, val=0, next=None):
#         self.val = val
#         self.next = next
class Solution:
    def mergeInBetween(self, list1: ListNode, a: int, b: int, list2: ListNode) -> ListNode:
        answer = currNode = list1
        currId = 0

        while currNode:
            currId += 1
            if currId == a:
                currId -= 1
                removeNode = copy.deepcopy(currNode)
                currNode.next = list2
                break
            else:
                currNode = currNode.next

        while removeNode:
            removeNode = removeNode.next
            currId += 1
            if currId == b:
                restNode = removeNode.next
                break

        while currNode:
            currNode = currNode.next
            if currNode.next is None:
                currNode.next = restNode
                break
        
        return answer
